mapper.select:
SELECT cl_Num, cl_Name, memberid, ck_Content, ck_State, loops
FROM CKList
WHERE memberId = #{memberId}

mapper.update:
UPDATE CKList
SET	   loops = #{loops}
WHERE cl_Num = #{cl_Num}

UPDATE CKList
SET		cl_Name = #{cl_Name},
		ck_Content = #{ck_Content},
		loops = #{loops}
WHERE cl_num = ${cl_num}
