mapper.select:
SELECT cl_Num, cl_Name, memberid, ck_Content, ck_State, loops
FROM CKList
WHERE memberId = #{memberId}

mapper.update:
UPDATE CKList
SET	   loops = #{loops}
WHERE cl_Num = #{cl_Num}

UPDATE CKList
SET		cl_Name = #{cl_Name},
		ck_Content = #{ck_Content},
		loops = #{loops}
WHERE cl_num = ${cl_num}

mapper.insert:
INSERT INTO PS_Work (
	PS_Num
	,PS_Name
	,PS_Content
	,memberId
	,start_Date
	,deadLine
	,Ps_Pri
	)
VALUES (
	ps_seq.nextval
	,#{ps_Name}
	,#{ps_Content}
	,#{memberId}
	,#{startDate}
	,#{deadLine}
	,#{ps_Pri}
	)

mapper.update:
UPDATE PS_Work
SET		ps_Num = #{ps_Num},
		ps_Content = #{ps_Content},
		ps_Pri = #{ps_Pri}
WHERE ps_Num = #{ps_Num}

UPDATE PS_Work
SET		progress = 1
WHERE ps_Num = #{ps_Num}
